require(['ckeditor'], function(ckeditor) {
  var translations = $jsontool.serialize($translations);
  for (var pluginName in translations) {
    if (translations.hasOwnProperty(pluginName)) {
      ckeditor.plugins.setLang(pluginName, '$xcontext.locale', translations[pluginName]);
    }
  }
});
